package com.reds.service.cassandraloaderapi.exception;
import com.reds.library.redscommons.exception.AbstractPlatformRuntimeException;

/**
 * <b>Purpose:</b> Runtime Exception class for Cassandraloader
 *
 * @author <b>SHINEED BASHEER</b>
 *
 *			This code is generated by Reds
 */
public class CassandraloaderRuntimeException extends AbstractPlatformRuntimeException {

	/**
	 * Default serialVersionId
	 */
	private static final long serialVersionUID = 1L;

	public CassandraloaderRuntimeException(String code) {
		super(code);
	}

	/**
	 * @param code - Error Code
	 * @param message
	 */
	public CassandraloaderRuntimeException(String code, String message) {
		super(message, code);

	}

	/**
	 * @param code  - Error Code
	 * @param message
	 * @param cause
	 */
	public CassandraloaderRuntimeException(String code, String message, Throwable cause) {
		super(code, message, cause);

	}

	/**
	 * @param code  - Error Code
	 * @param message
	 * @param cause
	 * @param enableSuppression
	 * @param writableStackTrace
	 */
	public CassandraloaderRuntimeException(String code, String message, Throwable cause, boolean enableSuppression,
			boolean writableStackTrace) {
		super(code, message, cause, enableSuppression, writableStackTrace);

	}

}